import React, { useState } from "react";

function ToDoList() {

    const [tasks, setTasks] = useState([{text: "Hallo!", isCompleted: false}]);
    const [newTask, setNewTask] = useState("");
    const [filter, setFilter] = useState("all")

    function handleKeyPress(event) {
        if (event.key == "Enter") {
            event.preventDefault();
            addTask();
        }
    }

    function handleInputChange(event) {
        setNewTask(event.target.value)
    }

    function addTask() {
        if (newTask.trim() !== "") {
            setTasks(t => [...t, { text: newTask, isCompleted: false }]);
            setNewTask('')
        }
    }

    function deleteTask(index) {
        const updatedTasks = tasks.filter((_, i) => i !== index)
        setTasks(updatedTasks);
    }

    function moveTaskUp(index) {
        if (index > 0) {
            const updatedTasks = [...tasks];
            [updatedTasks[index], updatedTasks[index - 1]] = [updatedTasks[index - 1], updatedTasks[index]];
            setTasks(updatedTasks);
        }
    }

    function moveTaskDown(index) {
        if (index < tasks.length - 1) {
            const updatedTasks = [...tasks];
            [updatedTasks[index], updatedTasks[index + 1]] = [updatedTasks[index + 1], updatedTasks[index]];
            setTasks(updatedTasks);
        }
    }

    function toggleCompleteTask(index) {
        const updatedTasks = tasks.map((task, i) => {
            return i === index ? {...task, isCompleted: !task.isCompleted} : task
        })
        setTasks(updatedTasks)
    }

    function handleFilterChange(event) {
        setFilter(newFilter)
    }

    const filteredTasks = tasks.filter((task) => {
        if (filter === "completed") return task.isCompleted;
        if (filter === "incomplete") return !task.isCompleted;
        return true;
    })

    return (
        <div className = "to-do-list">
            <h1>To-Do-List</h1>
            <div>
                <input
                    id="myInput"
                    type = "text"
                    placeholder = "Enter a task..."
                    value = { newTask }
                    onChange = { handleInputChange }
                    onKeyDown={handleKeyPress}/> 
                <button id = "myBtn" className="add-button" onClick={ addTask }>
                    Add    
                </button>  
                <div className="filter-select">
                    <label htmlFor="filter">Filter tasks:</label>
                    <select id="filter" value={filter} onChange={handleFilterChange}>
                        <option value="all">All</option>
                        <option value="completed">Completed</option>
                        <option value="incomplete">Incomplete</option>
                    </select>
                </div>             
            </div>

            <ol>
                {
                    tasks.map((task, index) => 
                        <li 
                            key = {index} 
                            className = {task.isCompleted ? "completed-task" : ""}
                        >
                            <input
                                type="checkbox"
                                checked={task.isCompleted}
                                onChange={() => toggleCompleteTask(index)}
                                className="my-checkbox"
                            />
                            <span className="text">{task.text}</span>
                            <button className="delete-button" onClick={() => deleteTask(index)}>
                                Delete
                            </button>
                            <button className="move-button" onClick={() => moveTaskUp(index)}>
                                UP
                            </button>
                            <button className="move-button" onClick={() => moveTaskDown(index)}>
                                DOWN
                            </button>
                        </li>
                    )
                }
            </ol>
        </div>
    )
}

export default ToDoList;
